import React, { Component } from "react";
import API from "../Utils/API"

class Employees extends Component {
   loadEmployees = () => {
      API.getRandomEmployee()
        .then(res =>
          this.setState({
            image: res.data.picture.medium,
            firstName: res.data.name.first,
            lastName: res.data.name.last,
            email: res.data.email,
            phone: res.data.phone,
            // location: res.data.location
          })
        )
        .catch(err => console.log(err));
    };
    renderTableData() {
        return this.state.employees.map((employees, index) => {
           const { image, firstName, lastName, phone, email  } = employees
           return (
              <tr key={index}>
                 <td>{image}</td>
                 <td>{firstName}{lastName}</td>
                 <td>{phone}</td>
                 <td>{email}</td>
                 {/* <td>{location}</td> */}
              </tr>
           )
        })
     }

     render() {
        return (
           <div>
              <h1 id='title'>Employee Directory</h1>
              <table id='employees'>
                 <tbody>

                    {this.renderTableData()}
                    {/* <tr key={this.email}>
                 <td>{this.image}</td>
                 <td>{this.firstName}{this.lastName}</td>
                 <td>{this.phone}</td>
                 <td>{this.email}</td> */}
                 {/* <td>{location}</td> */}
              {/* </tr> */}
                 </tbody>
              </table>
           </div>
        )
     }

}

export default Employees;
