import React, {useState, useEffect} from "react";
import API from "../../Utils/API"
import "./style.css";

const EmployeesTable = (props) => {
   const [filteredEmployees, setFilteredEmployees] = useState (filteredEmployees)
   useEffect(() => {
      API.loadEmployees()
      .then(res => res.json())
      .then((response) => setFilteredEmployees(response.results))
   },)
   return(
      <table className = "table table-bordered table-hover table-stripped text-center table-sortable">
         <thead>
            <tr>
               <th scope = "col">Image</th>
               <th scope = "col" data-field = "name" data-sortable = "true">
                  <div onClick = {() => props.sortBy("name", "first", "last")}>
                     Name
                  </div>
               </th>
               <th scope = "col">
                  <div onClick = {() => props.sortBy("email")}>
                     Email
                  </div>
               </th>
               <th scope = "col">
                  <div onClick = {() => props.sortBy("phone")}>
                     Phone
                  </div>
               </th>
            </tr>
         </thead>
         <tbody>
            {filteredEmployees.map((employee) => {
               const { first, last } = employee.name;
               const fullName = `${first} ${last}`;

               return(
                  <tr key = {employee.login.uuid}>
                     <td>
                        <img src = {employee.picture.medium} alt = {fullName}/>
                     </td>
                     <td className = "align-center">{fullName}</td>
                     <td className = "align-center"><a href={`mailto:${employee.email}`}>{employee.email}</a></td>
                     <td className = "align-center"><a href={`tel:+1${employee.phone}`}>{employee.phone}</a></td>
                  </tr>
               );
            })}
         </tbody>
      </table>
   )
}

export default EmployeesTable;
