            {filteredEmployees.filter(employee => employee.first.toUpperCase().includes(search.toUpperCase()) || employee.last.toUpperCase().includes(search.toUpperCase())).map(employee => {
               const { first, last } = employee.name;
               const fullName = `${first} ${last}`;

               return(
                  <tr key = {employee.login.uuid}>
                     <td>
                        <img src = {employee.picture.medium} alt = {fullName}/>
                     </td>
